import ItemTable from './components/ItemTable.vue';
import json from "./assets/SteamInventory.json";
export default {
  name: 'App',
  components: {
    ItemTable
  },
  methods: {
    countOccurencesForClassId() {
      let count = 0;
      for (let item in this.items) {
        if (item != null) count++;
      }
      return count;
    }
  },
  computed: {
    inventoryCount() {
      return Object.entries(json.assets).length;
    },
    descriptions() {
      return json.descriptions.map(desc => ({
        market_name: desc.market_name,
        class_id: desc.classid
      }));
    },
    items() {
      return json.assets.map(item => item.classid);
    }
  }
};
